	MEMBER_NO= #{memberNo}
	AND CART_NO IN

#{cartNo}

mapper.update:
		UPDATE 
			S_CART
    	SET 
    		BUY_COUNT =BUY_COUNT+#{buyCount}
		WHERE SP_NO=#{spNo}
		AND MEMBER_NO=#{memberNo}

UPDATE
	    S_ORDER
	SET
	    RES_NAME=#{resName}
	    ,SEND_NAME=#{sendName}
	    ,RES_PHONE=#{resPhone}
	    ,SUMPRICE=#{sumPrice}
	    ,ORDER_DAY=SYSDATE
	    ,ORDER_ADDRESS=#{address}
	WHERE
	    ORDER_NO=#{orderNo}


		UPDATE
   				 S_ORDER
			SET
   			    RES_NAME=#{resName}
                ,SEND_NAME=#{sendName}
    			,RES_PHONE=#{resPhone}
